import './card.styles.css';
import { useDispatch } from 'react-redux';
import { setFilter } from '../../app/slices/filterSlice';

const Card = ({ movie }) => {
  const { poster_path, original_title, email } = movie;
  const baseImageURL = 'https://image.tmdb.org/t/p/w220_and_h330_face'

  const dispatch = useDispatch();

  const openCard = (movie) => {
    console.log('openCard')
    console.log('movie', movie)
    dispatch(setFilter(movie))

  }

  return (
    <div className='card-container' onClick={openCard(movie)}>
      <img
        alt={`movie ${original_title}`}
        src={`${baseImageURL}${poster_path}`}
      />
      <h2>{original_title}</h2>
      <p>{email}</p>
    </div>
  );
};

export default Card;
