import { useState, useEffect } from 'react';
import './App.css';

import CarrouselList from './components/carrousel-list/carrousel-list.component';
import SearchBox from './components/search-box/search-box.component';

const App = () => {
  const [searchField, setSearchField] = useState('');
  const [movies, setMovies] = useState([]);
  const [page, setPage] = useState(0);
  const [filteredMovies, setFilterMovies] = useState(movies);

  useEffect(() => {
    fetch('https://api.themoviedb.org/3/discover/movie?api_key=bf62f8e705dfa887f81f57c4d118d7a0')
      .then((response) => response.json())
      .then((movies) => {
        setMovies(movies.results)
        setPage(movies.page)
      });
  }, []);

  useEffect(() => {
    console.log('useEffect')
    console.log('movies', movies)
    console.log('page', page)
    const newFilteredMovies = movies.filter((movie) => {
      return movie.original_title.toLocaleLowerCase().includes(searchField);
    });

    setFilterMovies(newFilteredMovies);
  }, [movies, searchField]);

  const onSearchChange = (event) => {
    const searchFieldString = event.target.value.toLocaleLowerCase();
    setSearchField(searchFieldString);
  };

  return (
    <div className='App'>
      <h1 className='app-title'>Movies</h1>

      <SearchBox
        className='movies-search-box'
        onChangeHandler={onSearchChange}
        placeholder='search movies'
      />
      <CarrouselList movies={filteredMovies} />
    </div>
  );
};

export default App;
