import Card from '../card/card.component';
import CardModal from '../card-modal/card-modal.component';
import { useState, useEffect } from 'react';
import './carrousel-list.styles.css';

const CarrouselList = ({ movies }) => {


  const [show, setShow] = useState(false)

  return (
    <div className='carrousel-list'>

      {movies.map((movie) => {
        return (
          <div>
            <CardModal key={movie.id} movie={movie} show={show}/>
            <Card key={movie.id} movie={movie} />
          </div>

        )
      })}
    </div>
  )

};

export default CarrouselList;
