import './card.styles.css';
import React, { useState } from 'react';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';

const Card = ({ movie }) => {
  const { poster_path, original_title, email } = movie;

  // Modal
  const [show, setShow] = useState(false);

  const handleClose = () => {
    console.log('closeMOdal')
    console.log('show',show)
    setShow(false);
  }
  const handleShow = () => setShow(true);

  useEffect(() => {
    console.log('useEffect show')


  }, [show]);

  const baseImageURL = 'https://image.tmdb.org/t/p/w220_and_h330_face'

  return (
    <div className='card-container' onClick={handleShow}>

      <img
        alt={`movie ${original_title}`}
        src={`${baseImageURL}${poster_path}`}
      />
      <h2>{original_title}</h2>
      <p>{email}</p>

      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Modal heading</Modal.Title>
        </Modal.Header>
        <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary" onClick={handleClose}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>

      <button type="button" class="btn btn-primary" data-toggle="modal" data-target="#exampleModalCenter">
        Launch demo modal
      </button>


      <div class="modal fade" id="exampleModalCenter" tabindex="-1" role="dialog" aria-labelledby="exampleModalCenterTitle" aria-hidden="true">
        <div class="modal-dialog modal-dialog-centered" role="document">
          <div class="modal-content">
            <div class="modal-header">
              <h5 class="modal-title" id="exampleModalLongTitle">Modal title</h5>
              <button type="button" class="close" data-dismiss="modal" aria-label="Close">
                <span aria-hidden="true">&times;</span>
              </button>
            </div>
            <div class="modal-body">
              ...
            </div>
            <div class="modal-footer">
              <button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
              <button type="button" class="btn btn-primary">Save changes</button>
            </div>
          </div>
        </div>
      </div>

    </div>



  );
};

export default Card;
